package com.base.system.action;import org.springframework.web.servlet.ModelAndView;import com.base.system.delegate.LayoutDelegate;public class LayoutAction extends ProjectAction {		public Object execute(String actionFlag, ModelAndView mav) throws Exception {				if(actionFlag.equals("r_layout_left_menu") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();						delegete.selectMenuList();						this.setForwardPage( "/jsp/common/outline/Outline.layout_left_menu.jsp" );		} else if(actionFlag.equals("r_layout_top_menu") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();						delegete.selectMenuList();						this.setForwardPage( "/jsp/common/outline/Outline.layout_top_menu.jsp" );		} else if(actionFlag.equals("r_main_frame") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();						delegete.selectMenuInfo();						this.setForwardPage( "/jsp/common/outline/Outline.main_frame.jsp" );		}  				return null;	}}