package com.base.system.delegate;import java.sql.SQLException;import javax.naming.NamingException;import project.jun.dao.HoDaoImpl;import project.jun.dao.parameter.HoQueryParameterHandler;import project.jun.dao.parameter.HoQueryParameterMap;import project.jun.dao.result.HoList;import project.jun.dao.result.HoMap;import project.jun.was.result.exception.HoException;public class LayoutDelegate extends ProjectDelegate {	/**	 * 메뉴 목록을 조회한다.	 * @throws NamingException	 * @throws SQLException	 * @throws HoException	 */	public void selectMenuList() throws NamingException, SQLException, HoException {		HoDaoImpl dao = super.getHoDaoImpl();				HoQueryParameterHandler hqph = new HoQueryParameterHandler(param);		 		HoQueryParameterMap value = hqph.getForDetail();						HoList listMenu = dao.queryForList("MenuDAO.selectAllMenuList", value);		param.infoForParameter();		param.info();				model.put("listMenu", listMenu);	}		/**	 * 메뉴 정보를  조회한다.	 * @throws NamingException	 * @throws SQLException	 * @throws HoException	 */	public void selectMenuInfo() throws NamingException, SQLException, HoException {		HoDaoImpl dao = super.getHoDaoImpl();				HoQueryParameterHandler hqph = new HoQueryParameterHandler(param);		 		HoQueryParameterMap value = hqph.getForDetail();						HoMap menuInfo = dao.queryForInfo("MenuDAO.selectMenuName", value);		param.infoForParameter();		param.info();				model.put("menuInfo", menuInfo);	}}