mapper.select:
SELECT COUNT(*) CNT
  FROM V_HR_MEMBER HM
 WHERE 1=1
   AND COMPANY_CD = #{COMPANY_CD}
 
   AND HM.SITE = #{S_SITE}
 
 
   AND UPPER(REPLACE(HM.NAME,' ','')) LIKE '%'||UPPER(REPLACE(#{MEMBER_NAME},' ',''))||'%'

SELECT BB.* ,
		BB.MEMBER_NO CODE, BB.NAME NAME, #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG
  FROM (
	SELECT ROWNUM RN, AA.* FROM (
		SELECT COMPANY_CD, MEMBER_NO, NAME, DEPT_CD, DEPT_NAME, RANK_CODE, RANK_NAME, JIKWI_CD, JIKWI_NM, EMAIL, TEL_NO1||'-'||TEL_NO2||'-'||TEL_NO3 TEL_NO
		
		  FROM V_HR_MEMBER HM
		 WHERE 1=1
		   AND COMPANY_CD = #{COMPANY_CD}
		 
		   AND HM.SITE = #{S_SITE}
		 
		 
		 	AND UPPER(REPLACE(HM.NAME,' ','')) LIKE '%'||UPPER(REPLACE(#{MEMBER_NAME},' ',''))||'%'
		 
		 
		 	ORDER BY ${SORT} ${DIR}
		 
		 
		 	ORDER BY NAME
		 
	) AA WHERE ROWNUM  <  #{ENDROWNUM}
) BB WHERE BB.RN  >  #{BEGINROWNUM}

SELECT COMPANY_CD, MEMBER_NO, NAME, DEPT_CD, DEPT_NAME, RANK_CODE, RANK_NAME, JIKWI_CD, JIKWI_NM
  FROM V_HR_MEMBER HM
 WHERE 1=1
   AND COMPANY_CD = #{COMPANY_CD}
   AND MEMBER_NO  = #{MEMBER_NO}

SELECT COUNT(*) AS CNT
  FROM V_HR_COURSE HC
 WHERE COMPANY_CD = #{COMPANY_CD}    
AND HC.COURSE_CODE = #{COURSE_CODE}
AND REPLACE(UPPER(HC.COURSE_NAME),' ','') LIKE '%'|| REPLACE(UPPER(#{COURSE_NAME}),' ','')||'%'

SELECT LIST.*, LIST.COURSE_CODE CODE, LIST.COURSE_NAME NAME, #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG FROM ( SELECT ROWNUM RN, SUB.* FROM ( SELECT COMPANY_CD --회사코드 ,COURSE_CODE --과정코드 ,COURSE_NAME --과정명 ,COURSE_NAME_DTL --과정명상세 ,PRODUCT_CODE --PRODUCT코드 ,CLASSIFY_CD --분류코드 ,(SELECT CLASSIFY_NM FROM HR_COURSE_CLASSIFY WHERE COMPANY_CD = HC.COMPANY_CD AND CLASSIFY_CD = HC.CLASSIFY_CODE3 ) CLASSIFY_CDNM --과정분류명 ,CLASSIFY_CODE1 --과정대분류코드 ,CLASSIFY_CODE2 --과정중분류코드 ,CLASSIFY_CODE3 --과정소분류코드 ,COURSE_GBN --과정구분(소유권구분) ,F_CODE_NAME(COMPANY_CD,COURSE_GBN)AS COURSE_GBNNM --과정구분명(소유권구분명) ,EDU_TYPE --교육형태 ,EDU_TIME --교육시간 ,COMEDU_TIME --교육인정시간 ,PERIOD --교육기간 ,PERIOD_GBN --교육기간구분 ,COST_AMT --수강료 ,REFUND_YN --고용보험여부 ,REFUND_AMT1 --대기업환급금액 ,REFUND_AMT2 --우선지원환급금액 ,NVL(REFUND_AMT1 + REFUND_AMT2,0)AS REFUND_SUM --환급금액 ,USE_YN --사용여부 ,REGI_DT --등록일 ,REGI_NO --등록자 ,MODI_DT --수정일 ,MODI_NO --수정자 FROM V_HR_COURSE HC WHERE COMPANY_CD = #{COMPANY_CD} AND HC.COURSE_CODE = #{COURSE_CODE} AND REPLACE(UPPER(HC.COURSE_NAME),' ','') LIKE '%'|| REPLACE(UPPER(#{COURSE_NAME}),' ','')||'%'
ORDER BY ${SORT} ${DIR}
ORDER BY COURSE_NAME
) SUB WHERE ROWNUM < #{ENDROWNUM} ) LIST WHERE RN > #{BEGINROWNUM}

SELECT COUNT(*) AS CNT
  FROM HR_BIZ_CLIENT HBC
 WHERE HBC.COMPANY_CD = #{COMPANY_CD}    			
AND HBC.BIZ_HNAME = #{CLIENT_NAME}

SELECT BB.* ,
		BB.CLIENT_CO_CODE CODE, BB.BIZ_HNAME NAME, #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG 
  FROM (
	SELECT ROWNUM RN, AA.*
	  FROM (SELECT HBC.COMPANY_CD      , 
					HBC.CLIENT_CO_CODE  , 
					HBC.BIZ_HNAME       , 
					HBC.BIZ_ENAME       , 
					HBC.BIZ_NO           
			  FROM HR_BIZ_CLIENT HBC
			 WHERE HBC.COMPANY_CD = #{COMPANY_CD}    			
			AND REPLACE(UPPER(HBC.BIZ_HNAME),' ','') LIKE '%'|| REPLACE(UPPER(#{CLIENT_NAME}),' ','')||'%'
			
				ORDER BY ${SORT} ${DIR}
			
			
				ORDER BY BIZ_HNAME
			
	)AA WHERE ROWNUM  <  #{ENDROWNUM}
) BB WHERE BB.RN  >  #{BEGINROWNUM}

SELECT COUNT(*) AS CNT
  FROM HR_USER HU
 WHERE HU.COMPANY_CD = #{COMPANY_CD}    			
AND HU.NAME = #{USER_NAME}

SELECT BB.*, BB.MEMBER_NO CODE, BB.NAME NAME , #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG FROM ( SELECT ROWNUM RN, AA.* FROM (SELECT HU.COMPANY_CD ,HU.MEMBER_NO ,HU.NAME ,HU.PWD ,HU.USER_GBN ,HU.COMPANY_NM ,HU.DEPT_NAME ,HU.JIKWI_NM ,HU.TEL_NO1 ,HU.TEL_NO2 ,HU.TEL_NO3 ,TEL_NO1||'-'||TEL_NO2||'-'||TEL_NO3 TEL_NO ,HU.HANDPHONE1 ,HU.HANDPHONE2 ,HU.HANDPHONE3 ,HU.FAX1 ,HU.FAX2 ,HU.FAX3 ,HU.EMAIL ,HU.NOTE ,HU.USE_YN ,HU.IP_ADDR ,HU.LOGIN_DT FROM HR_USER HU WHERE HU.COMPANY_CD = #{COMPANY_CD} AND REPLACE(UPPER(HU.NAMEE),' ','') LIKE '%'|| REPLACE(UPPER(#{USER_NAME}),' ','')||'%'
ORDER BY ${SORT} ${DIR}
ORDER BY NAME
)AA WHERE ROWNUM < #{ENDROWNUM} ) BB WHERE BB.RN > #{BEGINROWNUM}

SELECT BB.* ,
		BB.DEPT_CD CODE, BB.DEPT_NAME NAME, #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG
  FROM (
	SELECT ROWNUM RN, AA.* FROM (
		SELECT COMPANY_CD, DEPT_CD, DEPT_NAME, DEPT_MNG_NO, P_DEPT_CODE, DEPT_LEVEL, ORDER_NO
		  FROM V_HR_DEPT HD
		 WHERE 1=1
		   AND COMPANY_CD = #{COMPANY_CD}
	       AND USE_YN   = 'Y'
		 
		 	AND UPPER(REPLACE(HD.DEPT_NAME,' ','')) LIKE '%'||UPPER(REPLACE(#{DEPT_NAME},' ',''))||'%'
		 
		 
		 	AND HD.DEPT_LEVEL = #{DEPT_LEVEL}
		 
		 
		 	AND UPPER(REPLACE(HD.DEPT_CD,' ','')) LIKE '%'||UPPER(REPLACE(#{DEPT_CD},' ',''))||'%'
		 
		 
		 	ORDER BY ${SORT} ${DIR}, DEPT_NAME, ORDER_NO
		 
		 
		 	ORDER BY DEPT_NAME, ORDER_NO
		 
	) AA WHERE ROWNUM  <  #{ENDROWNUM}
) BB WHERE BB.RN  >  #{BEGINROWNUM}

SELECT COUNT(*) CNT FROM V_HR_MEMBER HM WHERE 1=1 AND COMPANY_CD = #{COMPANY_CD} AND USE_YN = 'Y'
AND HM.SITE = #{S_SITE}
AND UPPER(REPLACE(HM.NAME,' ','')) LIKE '%'||UPPER(REPLACE(#{MEMBER_NAME},' ',''))||'%'

SELECT BB.*,
       BB.RESNO||'|'||BB.NAME||'|'||BB.RANK_NAME||'|'||BB.DEPT_NAME||'|'||''||'|'||BB.EMAIL||'|'||BB.EPID CODE, #{FORMNAME} FORM_OBJ_NAME, #{OBJNAME} OBJ_NAME, #{P_ACTION_FLAG} P_ACTION_FLAG
  FROM (
	SELECT ROWNUM RN, AA.* FROM (
		SELECT COMPANY_CD, MEMBER_NO, NAME, DEPT_CD, DEPT_NAME, RANK_CODE, RANK_NAME, 
		       RESNO, JIKWI_CD, JIKWI_NM, EMAIL, NULL EPID
		  FROM V_HR_MEMBER HM
		 WHERE 1=1
		   AND COMPANY_CD = #{COMPANY_CD}
		   AND USE_YN     = 'Y'
		 
		   AND HM.SITE = #{S_SITE}
		 
		 
		 	AND UPPER(REPLACE(HM.NAME,' ','')) LIKE '%'||UPPER(REPLACE(#{MEMBER_NAME},' ',''))||'%'
		 
		 
		 	ORDER BY ${SORT} ${DIR}
		 
		 
		 	ORDER BY NAME
		 
	) AA WHERE ROWNUM  <  #{ENDROWNUM}
) BB WHERE BB.RN  >  #{BEGINROWNUM}
