mapper.select:
SELECT COUNT(*) CNT FROM ( SELECT OWNER, TABLE_NAME, TABLE_TYPE, COMMENTS, (SELECT COUNT(*) FROM ALL_TAB_COLUMNS WHERE OWNER = ATC.OWNER AND TABLE_NAME = ATC.TABLE_NAME ) COLUMN_CNT, ( SELECT COUNT(*) FROM ALL_CONSTRAINTS X, ALL_CONS_COLUMNS Y WHERE X.OWNER=ATC.OWNER AND X.TABLE_NAME=ATC.TABLE_NAME AND X.CONSTRAINT_TYPE = 'P' AND X.CONSTRAINT_NAME = Y.CONSTRAINT_NAME AND Y.OWNER = X.OWNER AND Y.TABLE_NAME = ATC.TABLE_NAME) PK_CNT FROM ALL_TAB_COMMENTS ATC WHERE 1=1
AND OWNER = #{S_OWNER}
AND TABLE_NAME NOT LIKE 'BIN%' -- AND OWNER NOT IN ('XDB','SYSTEM','SYS','OUTLN', 'DBSNMP') AND OWNER IN ('GLMS','GLMS2')
AND REPLACE(UPPER(TABLE_NAME),' ','') LIKE '%'|| REPLACE(UPPER(#{S_TABLE_NAME_item}),' ','')||'%'
AND REPLACE(UPPER(COMMENTS),' ','') LIKE '%'|| REPLACE(UPPER(#{S_COMMENT_NAME_item}),' ','')||'%'
)

SELECT LIST.* FROM (SELECT ROWNUM RNUM, SUB.* FROM (SELECT OWNER, TABLE_NAME, TABLE_TYPE, COMMENTS, (SELECT COUNT(*) FROM ALL_TAB_COLUMNS WHERE OWNER = ATC.OWNER AND TABLE_NAME = ATC.TABLE_NAME ) AS COLUMN_CNT, (SELECT COUNT(*) FROM ALL_CONSTRAINTS X, ALL_CONS_COLUMNS Y WHERE X.OWNER=ATC.OWNER AND X.TABLE_NAME=ATC.TABLE_NAME AND X.CONSTRAINT_TYPE = 'P' AND X.CONSTRAINT_NAME = Y.CONSTRAINT_NAME AND Y.OWNER = X.OWNER AND Y.TABLE_NAME = ATC.TABLE_NAME) AS PK_CNT FROM ALL_TAB_COMMENTS ATC WHERE 1 = 1
AND OWNER = #{S_OWNER}
AND TABLE_NAME NOT LIKE 'BIN%' -- AND OWNER NOT IN ('XDB','SYSTEM','SYS','OUTLN', 'DBSNMP') AND OWNER IN ('GLMS','GLMS2')
AND REPLACE(UPPER(TABLE_NAME),' ','') LIKE '%'|| REPLACE(UPPER(#{S_TABLE_NAME_item}),' ','')||'%'
AND REPLACE(UPPER(COMMENTS),' ','') LIKE '%'|| REPLACE(UPPER(#{S_COMMENT_NAME_item}),' ','')||'%'
ORDER BY ${SORT} ${DIR} ORDER BY OWNER ) SUB WHERE ROWNUM < #{ENDROWNUM} ) LIST WHERE RNUM > #{BEGINROWNUM}

	SELECT OWNER, TABLE_NAME, REPLACE(TRANSLATE(INITCAP(B.TABLE_NAME), B.TABLE_NAME||'abcdefghijklmnopqrstuvwxyz',B.TABLE_NAME),'_','') TABLE_AS, COMMENTS,
	       MAX(LENGTH(TABLE_NAME)) TABLE_NAME_LENGTH , REPLACE(INITCAP(B.TABLE_NAME),'_','') TABLE_AS_CAMEL
	  FROM ALL_TAB_COMMENTS B
	 WHERE 1=1 
	   AND B.OWNER IN (''
	 
		       #{OWNER_item}
	 )
	   AND B.TABLE_NAME IN  (''
	 
		       #{TABLE_NAME_item}
	 )
     GROUP BY OWNER, TABLE_NAME, COMMENTS, LENGTH(TABLE_NAME)

SELECT T_LENGTH, T_AS_LENGTH, MAX_COL_NAME_LENGTH
  FROM (
	SELECT MAX(T_AS_LENGTH) T_AS_LENGTH, MAX(T_LENGTH) T_LENGTH
      FROM ( 
	        	SELECT LENGTH(#{TABLE_AS_TABLE_item}) T_AS_LENGTH, LENGTH(#{TABLE_item}) T_LENGTH  FROM S_DUAL
	          )
	 ) T_L, (
	SELECT MAX(LENGTH(COLUMN_NAME))  MAX_COL_NAME_LENGTH
	  FROM ALL_TAB_COLUMNS B
	 WHERE 1=1 
	   AND B.OWNER IN (''
	 
		       #{OWNER_item}
	 )
	   AND B.TABLE_NAME IN  (''
	 
		       #{TABLE_item}
	 ) )
