mapper.select:
SELECT HU.COMPANY_CD COMPANY_CD, HU.MEMBER_NO USER_EMPID, HU.NAME USER_EMP_NAME, 'KR' COUNTRY, 'B' SYSTEM_CD, HM.DEPT_NAME DEPT_NM, 'Y' NEED_SSO_TRAY, HM.RANK_NAME, HM.EMAIL EMAIL, 'ko' LANGUAGE_CD, HM.EPID EPID FROM HR_USER HU, V_HR_MEMBER HM WHERE HU.COMPANY_CD IN (SELECT COMPANY_CD FROM HR_CLIENT) AND HU.MEMBER_NO = HM.MEMBER_NO AND HU.COMPANY_CD = HM.COMPANY_CD
AND HM.RESNO = #{NID}
AND HU.USE_YN = 'Y' AND HM.USE_YN = 'Y'

SELECT DECODE(COUNT(DECODE(USE_GROUP, 'C01', 1)), COUNT(*), 'Y', 'N') IS_ONLY_JO_BANJANG
  FROM S_USER_USERGRP
 WHERE COMPANY_CD = #{COMPANY_CD}
   AND MEMBER_NO  = #{USER_EMPID}

SELECT NVL(MIN(DATA_AUTH),6) DATA_AUTH
  FROM S_USER_USERGRP UU, USERGRP_MENU UM
 WHERE UU.COMPANY_CD = UM.COMPANY_CD
   AND UU.USE_GROUP  = UM.USE_GROUP
   AND UU.COMPANY_CD = #{COMPANY_CD}
   AND UU.MEMBER_NO  = #{USER_EMPID}

mapper.update:
INSERT INTO V_TB_AM_COM_SYSTEMLOG
(SYSTEM_ID   , RESNO       , LOGIN_TIME  , USER_IP     , LOGOUT_TIME )
SELECT 'ITII', RESNO       , SYSDATE     , #{IP}        , NULL        
  FROM V_HR_MEMBER HM
 WHERE COMPANY_CD = #{COMPANY_CD}
   AND MEMBER_NO  = #{USER_EMPID}
