package project.jun.delegate;import java.util.HashMap;import java.util.Map;import org.apache.log4j.Logger;import project.jun.dao.HoDaoImpl;import project.jun.was.config.HoConfig;import project.jun.was.parameter.HoParameter;import project.jun.was.spring.HoModel;public class HoDelegate {	public static final int PAGE_ROW_CNT = 20;		protected  Logger          logger     = Logger.getLogger(HoDelegate.class);	protected HoParameter param;		protected HoModel     model;	private   Map         daoMap = new HashMap();		/**	 * HoParameter를 담는다.	 * @return	 */	public HoParameter getHoParameter() {		return param;	}	public void setHoParameter(HoParameter parameter) {		this.param = parameter;	}	/**	 * 파라미터의 데이터 형태 yyyy/MM/dd or MM/dd/yyyy or MMM/dd/yyyy or yyyy-MM-dd	 * @return	 */	public String getParameterDateFormat() {		return this.param.getDefaut("DateFormat");	}	/**	 * HoModel를 담는다.	 * @return	 */	public HoModel getHoModel() {		return model;	}	public void setHoModel(HoModel model) {		this.model = model;				Map displayMap = param.getHoConfig().getDisplayFormat();		displayMap.put("YMD", this.param.getDefaut("DateFormat"));				this.model.setDisplayFormat(displayMap);	}		/**	 * 프로젝트에 기본으로 사용되는 db정보를 조회한다.	 * @return	 */	public HoDaoImpl getHoDaoImpl() {		HoDaoImpl dao = (HoDaoImpl)  this.daoMap.get("ProjectDao");			return dao;	}		/**	 * daoName에 해당하는 db정보를 조회한다.	 * @param daoName	 * @return	 */	public HoDaoImpl getHoDaoImpl(String daoName) {		HoDaoImpl dao = (HoDaoImpl)  this.daoMap.get(daoName);				return dao;	}	/**	 * 모든 dao정보를 조회한다.	 * @return	 */	public Map getHoDaoMap() {		return this.daoMap;	}		/**	 * dao정보를 입력한다.	 * @param daoMap	 */	public void setHoDaoMap(Map daoMap) {		if( this.daoMap == null ) {			this.daoMap = new HashMap();		}				this.daoMap.putAll(daoMap);	}		public HoConfig getHoConfig() {		return this.getHoParameter().getHoConfig();	}	}