package project.jun.doc.excel;import java.io.File;import java.io.FileNotFoundException;import java.io.IOException;import java.io.InputStream;import java.util.Date;public interface HoExcelReader {    public void setExelFile(InputStream is) throws FileNotFoundException, IOException;    public void setExelFile(String fileName) throws FileNotFoundException, IOException;    public void setExelFile(File file) throws FileNotFoundException, IOException ;    public void setSheet(int sheetIdx) throws FileNotFoundException, IOException ;    public Object getRow(int rowIdx) throws FileNotFoundException, IOException ;    public Object getCell(int rowIdx, short cellIdx) throws FileNotFoundException, IOException ;        public int getLastRowNum();    public int getLastRowNum(int sheetIdx) throws FileNotFoundException, IOException ;    public int getLastCellNum(int rowIdx) throws FileNotFoundException, IOException ;    public int getLastCellNum(int sheetIdx, int rowIdx) throws FileNotFoundException, IOException ;    public Object getValueRichTextString (int rowIdx, short cellIdx  ) throws FileNotFoundException, IOException ;    public String getStringValue (int rowIdx, short cellIdx  ) throws FileNotFoundException, IOException ;    public double getDoubleValue (int rowIdx, short cellIdx  ) throws FileNotFoundException, IOException ;     public Date getDateValue (int rowIdx, short cellIdx  ) throws FileNotFoundException, IOException ;    public Object getValue( int rowIdx, short cellIdx ) throws FileNotFoundException, IOException ;        public short getCellNum(String cellName) ;}