package project.jun.util;import java.util.ArrayList;public class HoArrayList extends ArrayList {	/**	 * 	 */	private static final long serialVersionUID = -8117473834728040739L;	public boolean add(int o) {		return super.add(new Integer(o));	}		public boolean add(short o) {		return super.add(new Short(o));	}	public boolean add(long o) {		return super.add(new Long(o));	}	public boolean add(double o) {		return super.add(new Double(o));	}	public boolean add(float o) {		return super.add(new Float(o));	}	public boolean add(boolean o) {		return super.add(new Boolean(o));	}		public String [] toStringArray() {		String [] strs = new String[this.size()];				for( int i=0 ; i<this.size() ; i++ ) {			strs[i] = this.get(i).toString();		}				return strs;	}}